<style lang="scss" scoped>
.project-card {
  position: relative;
  width: 36.5rem;
  height: 59.3rem;
  border: 1px solid var(--col-second);
  .card-img {
    background-image: url("/src/assets/media/Images/fitImg.jpeg");
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    width: 100%;
    height: 100%;
    filter: brightness(0);
    transition: all 0.2s ease-in-out;
  }

  .card-content {
    position: absolute;
    width: 100%;
    height: 100%;
    left: 0;
    top: 0;
    display: flex;
    justify-content: space-around;
    align-items: center;
    flex-direction: column;
    padding: 0 5rem;
    transition: all 0.2s ease-in-out;
    svg {
      fill: #818181;
      transition: all 0.2s ease-in-out;
    }
    .card-text {
      transition: all 0.2s ease-in-out;
      font-family: var(--ft-roboto);
      font-weight: 300;
      font-size: var(--fs-16);
      color: var(--col-second);
      line-height: 1.9rem;
      text-align: center;
    }
    .show-more {
      transition: all 0.2s ease-in-out;
      font-family: var(--ft-roboto);
      font-weight: bold;
      font-size: var(--fs-16);
      color: var(--col-second);
      line-height: 1.9rem;
      text-align: center;
    }
    &:hover svg {
      fill: #fff !important;
    }
  }
  &:hover .card-img {
    filter: brightness(1);
  }
  &:hover .card-text {
    color: var(--col-white);
  }
  &:hover .show-more {
    color: transparent;
  }
}
